create table pro_user_role (  user_id INTEGER ,  role_id INTEGER,  CREATE_DATE DATE DEFAULT now() not null ,  DONE_DATE DATE DEFAULT now() not null ,  LOSE_DATE DATE DEFAULT date_format('2099-12-31 00:00:00', 'yyyy-MM-dd HH24:mi:ss') not null ,  STATE varchar(1) default 'U' not null);create table pro_role (  role_id Integer primary key auto_increment,  role_name varchar(200) not null ,  CREATE_DATE DATE DEFAULT now() not null ,  DONE_DATE DATE DEFAULT now() not null ,  LOSE_DATE DATE DEFAULT date_format('2099-12-31 00:00:00', 'yyyy-MM-dd HH24:mi:ss') not null ,  STATE varchar(1) default 'U' not null);create table pro_role_menu (  menu_id INTEGER ,  role_id INTEGER,  CREATE_DATE DATE DEFAULT now() not null ,  DONE_DATE DATE DEFAULT now() not null ,  LOSE_DATE DATE DEFAULT date_format('2099-12-31 00:00:00', 'yyyy-MM-dd HH24:mi:ss') not null ,  STATE varchar(1) default 'U' not null);create table pro_menu (  MENU_ID INTEGER primary key auto_increment,  MENU_NAME varchar(200) not null ,  MENU_URL varchar(200) not null ,  MENU_LABEL varchar(200),  DISPLAY_ORDER INTEGER default 0,  PARENT_ID INTEGER default 0,  CREATE_DATE DATE DEFAULT now() not null ,  DONE_DATE DATE DEFAULT now() not null ,  LOSE_DATE DATE DEFAULT date_format('2099-12-31 00:00:00', 'yyyy-MM-dd HH24:mi:ss') not null ,  STATE varchar(1) default 'U' not null);