/** * $Id: DataSourceConfiguration.java,v 1.0 18/6/22 下午9:47 chenmin Exp $ * <p> */package com.profession.commons.conf;import com.alibaba.druid.pool.DruidDataSource;import org.springframework.boot.autoconfigure.jdbc.DataSourceBuilder;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.jdbc.datasource.DataSourceTransactionManager;import org.springframework.transaction.annotation.EnableTransactionManagement;import javax.sql.DataSource;/** * 数据源配置 * @author chenmin * @version $Id: DataSourceConfiguration.java,v 1.1 18/6/22 下午9:47 chenmin Exp $ * Created on 18/6/22 下午9:47 */@Configuration@EnableTransactionManagementpublic class DataSourceConfiguration {    @Bean    @ConfigurationProperties(prefix = "spring.datasource")    public DataSource dataSource() {        return DataSourceBuilder.create().type(DruidDataSource.class).build();    }    @Bean    public DataSourceTransactionManager transactionManager(DataSource dataSource) {        DataSourceTransactionManager transactionManager = new DataSourceTransactionManager();        transactionManager.setDataSource(dataSource);        return transactionManager;    }    @Bean    public JdbcTemplate jdbcTemplate(DataSource dataSource) {        return new JdbcTemplate(dataSource);    }}