/** * $Id: WebMvcConfiguration.java,v 1.0 18/6/22 下午9:56 chenmin Exp $ * <p> */package com.profession.commons.conf;import com.alibaba.fastjson.support.spring.FastJsonHttpMessageConverter;import org.springframework.context.annotation.Configuration;import org.springframework.http.converter.HttpMessageConverter;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;import java.util.List;/** * Web环境配置 * @author chenmin * @version $Id: WebMvcConfiguration.java,v 1.1 18/6/22 下午9:56 chenmin Exp $ * Created on 18/6/22 下午9:56 */@Configurationpublic class WebMvcConfiguration extends WebMvcConfigurerAdapter {    @Override    public void configureMessageConverters(List<HttpMessageConverter<?>> converters) {        FastJsonHttpMessageConverter httpMessageConverter = new FastJsonHttpMessageConverter();        converters.add(httpMessageConverter);        super.configureMessageConverters(converters);    }}